const MENU_BREAKPOINT=768,MENU_OPENED_CLASS="header__nav--open";window.addEventListener("DOMContentLoaded",(()=>{const toggleButton=document.querySelector("#toggle"),menu=document.querySelector("#menu");toggleButton.addEventListener("click",(()=>{menu.classList.contains(MENU_OPENED_CLASS)?menu.classList.remove(MENU_OPENED_CLASS):menu.classList.add(MENU_OPENED_CLASS)}))})),window.addEventListener("resize",(()=>{const windowWidth=window.innerWidth,menu=document.querySelector("#menu");windowWidth>768&&menu.classList.contains(MENU_OPENED_CLASS)&&menu.classList.remove(MENU_OPENED_CLASS)}));